import { NextRequest, NextResponse } from 'next/server';

// Ensure Node.js runtime if any Admin SDK is introduced here in future
export const runtime = 'nodejs';
import { verifyWithBackend, setSessionCookies, clearSessionCookies } from '@/lib/auth/session';

export async function POST(request: NextRequest) {
  try {
    const { idToken } = await request.json();

    if (!idToken) {
      return NextResponse.json(
        { error: 'ID token is required' },
        { status: 400 }
      );
    }

    // Verify the Firebase ID token with the backend
    const sessionData = await verifyWithBackend(idToken);

    // Create response with session data
    const response = NextResponse.json({
      success: true,
      user: sessionData.user,
    });

    // Set secure session cookies
    setSessionCookies(sessionData, response);

    return response;
  } catch (error: any) {
    console.error('Session creation error:', error);

    return NextResponse.json(
      { 
        error: 'Authentication failed',
        details: error.message 
      },
      { status: 401 }
    );
  }
}

export async function DELETE() {
  try {
    // Create response for logout
    const response = NextResponse.json({
      success: true,
      message: 'Logged out successfully',
    });

    // Clear session cookies
    clearSessionCookies(response);

    return response;
  } catch (error: any) {
    console.error('Logout error:', error);

    return NextResponse.json(
      { error: 'Logout failed' },
      { status: 500 }
    );
  }
}

export async function GET() {
  try {
    // This endpoint can be used to check if the user is authenticated
    // by checking if the session cookies exist and are valid
    const { isSessionValid } = await import('@/lib/auth/session');
    const isValid = await isSessionValid();

    const response = NextResponse.json({
      authenticated: isValid,
      message: isValid ? 'Session is valid' : 'No valid session',
    });

    return response;
  } catch (error: any) {
    console.error('Session check error:', error);

    return NextResponse.json(
      { error: 'Session check failed' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import exerciseDataService from '@/lib/services/exercise-data';
import { ExerciseFilters } from '@/types/exercise';
import { EXERCISE_LIBRARY_PAGE_SIZE } from '@/lib/constants/pagination';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);

    // Parse query parameters
    const page = parseInt(searchParams.get('page') || '1');
    const limit = parseInt(searchParams.get('limit') || EXERCISE_LIBRARY_PAGE_SIZE.toString());

    // Build filters from search params
    const filters: ExerciseFilters = {};

    const search = searchParams.get('search');
    if (search) {
      filters.search = search;
    }

    const type = searchParams.get('type');
    if (type) {
      filters.exerciseType = type;
    }

    const muscle = searchParams.get('muscle');
    if (muscle) {
      filters.primaryMuscleGroup = muscle;
    }

    const movement = searchParams.get('movement');
    if (movement) {
      filters.force = movement as 'Push' | 'Pull' | 'Static';
    }

    const difficulty = searchParams.get('difficulty');
    if (difficulty) {
      filters.level = difficulty as 'Beginner' | 'Intermediate' | 'Expert';
    }

    // Validate pagination parameters
    if (page < 1 || limit < 1 || limit > 100) {
      return NextResponse.json(
        { error: 'Invalid pagination parameters' },
        { status: 400 }
      );
    }

    // Fetch exercises
    const result = await exerciseDataService.getExercises(filters, page, limit);

    // Return the result
    return NextResponse.json(result);

  } catch (error) {
    console.error('API Error fetching exercises:', error);

    return NextResponse.json(
      { 
        error: 'Failed to fetch exercises',
        exercises: [],
        total: 0,
        page: 1,
        limit: EXERCISE_LIBRARY_PAGE_SIZE
      },
      { status: 500 }
    );
  }
}

