"use client";

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import Link from "next/link";
import { CookieConsent, defaultConsent, getStoredConsent, storeConsent } from "@/lib/cookies";
import { Cookie } from "lucide-react";

export function CookieBanner() {
  const [open, setOpen] = useState(false);
  const [showBanner, setShowBanner] = useState(false);
  const [consent, setConsent] = useState<CookieConsent>(defaultConsent);

  useEffect(() => {
    // Check if user has already made a choice
    const storedConsent = getStoredConsent();
    if (!storedConsent) {
      setShowBanner(true);
    } else {
      setConsent(storedConsent);
    }
  }, []);

  const handleAcceptAll = () => {
    const newConsent: CookieConsent = {
      necessary: true,
      analytics: true,
      marketing: true,
    };
    setConsent(newConsent);
    storeConsent(newConsent);
    setShowBanner(false);
    setOpen(false);
  };

  const handleRejectAll = () => {
    const newConsent: CookieConsent = {
      ...defaultConsent,
      necessary: true,
    };
    setConsent(newConsent);
    storeConsent(newConsent);
    setShowBanner(false);
    setOpen(false);
  };

  const handleSavePreferences = () => {
    storeConsent(consent);
    setShowBanner(false);
    setOpen(false);
  };

  const handleToggle = (key: keyof CookieConsent) => {
    if (key === 'necessary') return; // Cannot toggle necessary cookies
    setConsent(prev => ({
      ...prev,
      [key]: !prev[key],
    }));
  };

  if (!showBanner && !open) return null;

  return (
    <>
      {/* Main Banner */}
      {showBanner && (
        <div className="fixed bottom-0 left-0 right-0 bg-background border-t z-50 py-4 px-4 md:px-6">
          <div className="container mx-auto flex flex-col md:flex-row items-center justify-between gap-4">
            <div className="flex items-center gap-3">
              <Cookie className="h-5 w-5 text-primary" />
              <p className="text-sm">
                We use cookies to enhance your experience. By continuing to visit this site you agree to our use of cookies.{' '}
                <Link href="/cookies" className="text-primary hover:underline">
                  Learn more
                </Link>
              </p>
            </div>
            <div className="flex items-center gap-3">
              <Button variant="outline" size="sm" onClick={() => setOpen(true)}>
                Customize
              </Button>
              <Button variant="outline" size="sm" onClick={handleRejectAll}>
                Reject All
              </Button>
              <Button size="sm" onClick={handleAcceptAll}>
                Accept All
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Preferences Dialog */}
      <Dialog open={open} onOpenChange={setOpen}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>Cookie Preferences</DialogTitle>
            <DialogDescription>
              Customize your cookie preferences. Some cookies are necessary for the website to function and cannot be disabled.
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-6 py-4">
            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Necessary Cookies</Label>
                <p className="text-sm text-muted-foreground">
                  Required for the website to function properly
                </p>
              </div>
              <Switch checked disabled />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Analytics Cookies</Label>
                <p className="text-sm text-muted-foreground">
                  Help us understand how you use our website
                </p>
              </div>
              <Switch
                checked={consent.analytics}
                onCheckedChange={() => handleToggle('analytics')}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Marketing Cookies</Label>
                <p className="text-sm text-muted-foreground">
                  Used to deliver personalized advertisements
                </p>
              </div>
              <Switch
                checked={consent.marketing}
                onCheckedChange={() => handleToggle('marketing')}
              />
            </div>
          </div>

          <div className="flex justify-end gap-3">
            <Button variant="outline" onClick={handleRejectAll}>
              Reject All
            </Button>
            <Button variant="outline" onClick={handleAcceptAll}>
              Accept All
            </Button>
            <Button onClick={handleSavePreferences}>
              Save Preferences
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

'use client';

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Search, Loader2 } from 'lucide-react';
import { Input } from '../ui/input';
import { ExerciseFilters } from '@/types/exercise';
import { useDebouncedSearch } from '@/hooks/useDebouncedSearch';

interface DebouncedSearchInputProps {
  initialValue?: string;
  filters: ExerciseFilters;
  onSearchResults: (results: any) => void;
  onSearchStatusChange: (status: 'idle' | 'loading' | 'success' | 'error', error?: string | null) => void;
  placeholder?: string;
  className?: string;
  disabled?: boolean;
}

const DebouncedSearchInput: React.FC<DebouncedSearchInputProps> = ({
  initialValue = '',
  filters,
  onSearchResults,
  onSearchStatusChange,
  placeholder = 'Search exercises...',
  className = '',
  disabled = false,
}) => {
  const [inputValue, setInputValue] = useState(initialValue);
  const inputRef = useRef<HTMLInputElement>(null);

  const {
    term,
    onTermChange,
    onEnter,
    results,
    status,
    error,
    isPending,
    isComposing,
    setIsComposing,
  } = useDebouncedSearch(filters, {
    delayDesktop: 250,  // Faster response
    delayMobile: 350,   // Faster response on mobile
    minLength: 1,       // Search with just 1 character for better UX
  });

  // Handle input change - never blocks typing
  const handleInputChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;

    // Immediately update local input state (never blocks)
    setInputValue(value);

    // Don't trigger search during IME composition
    if (isComposing) {
      return;
    }

    // Trigger debounced search via hook
    onTermChange(value);
  }, [isComposing, onTermChange]);

  // Handle Enter key press - immediate search, bypass debounce
  const handleKeyDown = useCallback((e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      // Trigger immediate search
      onEnter();
    }
  }, [onEnter]);

  // Handle IME composition events
  const handleCompositionStart = useCallback(() => {
    setIsComposing(true);
  }, [setIsComposing]);

  const handleCompositionEnd = useCallback((e: React.CompositionEvent<HTMLInputElement>) => {
    setIsComposing(false);

    // Trigger change after composition ends
    const value = (e.target as HTMLInputElement).value;
    setInputValue(value);

    // Start debounced search after composition
    onTermChange(value);
  }, [setIsComposing, onTermChange]);

  // Sync input value with hook term when needed
  useEffect(() => {
    if (term !== inputValue && !isComposing) {
      setInputValue(term);
    }
  }, [term, inputValue, isComposing]);

  // Effect to handle search results - non-blocking
  useEffect(() => {
    if (results) {
      onSearchResults(results);
    }
  }, [results, onSearchResults]);

  // Effect to handle status changes - non-blocking
  useEffect(() => {
    onSearchStatusChange(status, error);
  }, [status, error, onSearchStatusChange]);

  // Helper text logic
  const getHelperText = useCallback(() => {
    const trimmedValue = inputValue.trim();

    if (trimmedValue.length === 0) {
      return null;
    }

    if (status === 'loading') {
      return 'Searching...';
    }

    if (status === 'error' && error) {
      return error;
    }

    return null;
  }, [inputValue, status, error]);

  const helperText = getHelperText();
  const showSpinner = status === 'loading' && !isPending; // Only show spinner for urgent loading

  return (
    <div className="relative">
      <div className="relative">
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
        <Input
          ref={inputRef}
          type="text"
          placeholder={placeholder}
          value={inputValue}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          onCompositionStart={handleCompositionStart}
          onCompositionEnd={handleCompositionEnd}
          className={`pl-10 pr-10 bg-white border-gray-300 focus:border-gray-400 focus:ring-gray-400 text-gray-900 placeholder:text-gray-500 ${className}`}
          disabled={disabled} // Never disable during search - input stays responsive
          role="searchbox"
          aria-label="Search exercises"
          aria-describedby={helperText ? 'search-helper' : undefined}
        />

        {/* Loading spinner - only show for urgent loading, not during transitions */}
        {showSpinner && (
          <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
            <Loader2 className="h-4 w-4 animate-spin text-gray-400" aria-live="polite" />
          </div>
        )}
      </div>

      {/* Helper text */}
      {helperText && (
        <p id="search-helper" className="text-xs text-gray-500 mt-1 ml-1">
          {helperText}
        </p>
      )}
    </div>
  );
};

export default DebouncedSearchInput;

import Link from "next/link";
import Image from "next/image";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
