          <div className="text-gray-700 leading-relaxed mb-6 flex-1 line-clamp-3">
            <p className="line-clamp-3">
              {exercise.content?.seo_description || `Learn proper form and technique for ${displayTitle}. Target your ${exercise.primaryMuscleGroup} with this effective exercise.`}
            </p>
          </div>

          {/* View Details Button */}
          <div className="mt-auto">
            <Button 
              className="bg-gray-800 hover:bg-gray-700 text-white px-5 py-2.5 rounded-lg font-medium transition-colors duration-200 shadow-sm hover:shadow-md w-full sm:w-auto"
            >
              View Details
              <ArrowRight className="w-4 h-4 ml-2" />
            </Button>
          </div>
        </div>
      </Link>
    </Card>
  );
}

'use client';

import React from 'react';
import { Button } from '../ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Alert, AlertDescription } from '../ui/alert';

interface ErrorBoundaryState {
  hasError: boolean;
  error?: Error;
}

interface ExerciseErrorBoundaryProps {
  children: React.ReactNode;
  fallback?: React.ReactNode;
}

class ExerciseErrorBoundary extends React.Component<ExerciseErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ExerciseErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    console.error('Exercise Error Boundary caught an error:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <Card>
          <CardHeader>
            <CardTitle>Something went wrong</CardTitle>
          </CardHeader>
          <CardContent>
            <Alert variant="destructive">
              <AlertDescription>
                An error occurred while loading this exercise. Please try refreshing the page.
              </AlertDescription>
            </Alert>

            <div className="mt-4 flex gap-2">
              <Button 
                onClick={() => window.location.reload()}
                variant="outline"
                size="sm"
              >
                Refresh Page
              </Button>
              <Button 
                onClick={() => this.setState({ hasError: false })}
                variant="outline"
                size="sm"
              >
                Try Again
              </Button>
            </div>

            {process.env.NODE_ENV === 'development' && this.state.error && (
              <details className="mt-4 p-4 bg-muted rounded-md">
                <summary className="cursor-pointer font-medium">Error Details</summary>
                <pre className="mt-2 text-sm text-muted-foreground overflow-auto">
                  {this.state.error.toString()}
                </pre>
              </details>
            )}
          </CardContent>
        </Card>
      );
    }

    return this.props.children;
  }
}

export default ExerciseErrorBoundary;

'use client';

import React from 'react';
import { Search, Filter, X } from 'lucide-react';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '../ui/select';
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from '../ui/collapsible';
import { ExerciseFilters as Filters, FilterOptions } from '../../types/exercise';
import DebouncedSearchInput from './DebouncedSearchInput';

interface ExerciseFiltersProps {
  filters: Filters;
  filterOptions: FilterOptions;
  onFiltersChange: (filters: Filters) => void;
  onClearFilters: () => void;
  onSearchResults?: (results: any) => void;
  onSearchStatusChange?: (status: 'idle' | 'loading' | 'success' | 'error', error?: string | null) => void;
  isLoading?: boolean;
}

const ExerciseFilters: React.FC<ExerciseFiltersProps> = ({
  filters,
  filterOptions,
  onFiltersChange,
  onClearFilters,
  onSearchResults,
  onSearchStatusChange,
  isLoading = false,
}) => {
  const [isOpen, setIsOpen] = React.useState(false);

  // Filter out empty strings from filter options
  const sanitizedFilterOptions = React.useMemo(() => {
    return {
      exerciseTypes: filterOptions.exerciseTypes.filter(type => type && type.trim() !== ''),
      primaryMuscleGroups: filterOptions.primaryMuscleGroups.filter(muscle => muscle && muscle.trim() !== ''),
      forces: filterOptions.forces.filter(force => force && force.trim() !== ''),
      levels: filterOptions.levels.filter(level => level && level.trim() !== ''),
      categories: filterOptions.categories.filter(category => category && category.trim() !== ''),
      equipment: filterOptions.equipment.filter(equipment => equipment && equipment.trim() !== ''),
      mechanics: filterOptions.mechanics.filter(mechanic => mechanic && mechanic.trim() !== ''),
    };
  }, [filterOptions]);

  const handleSearchChange = (searchValue: string) => {
    onFiltersChange({
      ...filters,
      search: searchValue,
    });
  };

  const handleFilterChange = (key: keyof Filters, value: string | undefined) => {
    onFiltersChange({
      ...filters,
      [key]: value,
    });
  };

  const hasActiveFilters = () => {
    return (
      filters.search || 
      filters.exerciseType || 
      filters.primaryMuscleGroup || 
      filters.force || 
      filters.level
    );
  };

  const getActiveFilters = () => {
    const activeFilters: React.ReactNode[] = [];

    if (filters.search) {
      activeFilters.push(
        <Badge key="search" variant="outline" className="gap-1 bg-gray-100 text-gray-800 border-gray-300 hover:bg-gray-200">
          Search: {filters.search}
          <X 
            className="h-3 w-3 cursor-pointer hover:text-gray-600" 
            onClick={() => handleFilterChange('search', undefined)}
          />
        </Badge>
      );
    }

    if (filters.exerciseType) {
      activeFilters.push(
        <Badge key="type" variant="outline" className="gap-1 bg-gray-100 text-gray-800 border-gray-300 hover:bg-gray-200">
          Type: {filters.exerciseType}
          <X 
            className="h-3 w-3 cursor-pointer hover:text-gray-600" 
            onClick={() => handleFilterChange('exerciseType', undefined)}
          />
        </Badge>
      );
    }

    if (filters.primaryMuscleGroup) {
      activeFilters.push(
        <Badge key="muscle" variant="outline" className="gap-1 bg-gray-100 text-gray-800 border-gray-300 hover:bg-gray-200">
          Muscle: {filters.primaryMuscleGroup}
          <X 
            className="h-3 w-3 cursor-pointer hover:text-gray-600" 
            onClick={() => handleFilterChange('primaryMuscleGroup', undefined)}
          />
        </Badge>
      );
    }

    if (filters.force) {
      activeFilters.push(
        <Badge key="force" variant="outline" className="gap-1 bg-gray-100 text-gray-800 border-gray-300 hover:bg-gray-200">
          {filters.force}
          <X 
            className="h-3 w-3 cursor-pointer hover:text-gray-600" 
            onClick={() => handleFilterChange('force', undefined)}
          />
        </Badge>
      );
    }

    if (filters.level) {
      activeFilters.push(
        <Badge key="level" variant="outline" className="gap-1 bg-gray-100 text-gray-800 border-gray-300 hover:bg-gray-200">
          Level: {filters.level}
          <X 
            className="h-3 w-3 cursor-pointer hover:text-gray-600" 
            onClick={() => handleFilterChange('level', undefined)}
          />
        </Badge>
      );
    }

    return activeFilters;
  };

  return (
    <Card className="bg-white border-gray-200 shadow-md">
      <CardHeader>
        <CardTitle className="text-lg font-bold text-gray-900 flex items-center gap-2">
          <Search className="h-5 w-5" />
          Search & Filter Exercises
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Search Bar */}
        <DebouncedSearchInput
          initialValue={filters.search || ''}
          filters={filters}
          onSearchResults={onSearchResults || (() => {})}
          onSearchStatusChange={onSearchStatusChange || (() => {})}
          placeholder="Search exercises..."
          disabled={false} // Never disable - input must stay responsive during search
        />

        {/* Active Filters */}
        {hasActiveFilters() && (
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <h3 className="text-sm font-medium text-gray-700">Active Filters</h3>
              <Button
                variant="ghost"
                size="sm"
                onClick={onClearFilters}
                className="text-xs text-gray-500 hover:text-gray-700"
              >
                Clear All
              </Button>
            </div>
            <div className="flex flex-wrap gap-2">
              {getActiveFilters()}
            </div>
          </div>
        )}

        {/* Collapsible Filter Options */}
        <Collapsible open={isOpen} onOpenChange={setIsOpen}>
          <CollapsibleTrigger asChild>
            <Button 
              variant="outline" 
              className="w-full justify-between bg-white border-gray-300 hover:bg-gray-50 text-gray-700 hover:text-gray-900"
            >
              <span className="flex items-center gap-2 text-gray-700">
                <Filter className="h-4 w-4" />
                Filter Options
              </span>
              <span className="text-xs text-gray-500">
                {isOpen ? 'Hide' : 'Show'} Filters
              </span>
            </Button>
          </CollapsibleTrigger>

          <CollapsibleContent className="space-y-4 mt-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Exercise Type Filter */}
              <div>
                <label className="text-sm font-medium mb-2 block text-gray-700">
                  Exercise Type
                </label>
                <Select
                  value={filters.exerciseType || undefined}
                  onValueChange={(value) => handleFilterChange('exerciseType', value === 'all' ? undefined : value)}
                  disabled={isLoading}
                >
                  <SelectTrigger className="bg-white border-gray-300 hover:border-gray-400 text-gray-900">
                    <SelectValue placeholder="All types" />
                  </SelectTrigger>
                  <SelectContent className="bg-white border-gray-200 shadow-md">
                    <SelectItem value="all" className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">All types</SelectItem>
                    {sanitizedFilterOptions.exerciseTypes.map((type) => (
                      <SelectItem key={type} value={type} className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">
                        {type}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Primary Muscle Group Filter */}
              <div>
                <label className="text-sm font-medium mb-2 block text-gray-700">
                  Primary Muscle Group
                </label>
                <Select
                  value={filters.primaryMuscleGroup || undefined}
                  onValueChange={(value) => handleFilterChange('primaryMuscleGroup', value === 'all' ? undefined : value)}
                  disabled={isLoading}
                >
                  <SelectTrigger className="bg-white border-gray-300 hover:border-gray-400 text-gray-900">
                    <SelectValue placeholder="All muscle groups" />
                  </SelectTrigger>
                  <SelectContent className="bg-white border-gray-200 shadow-md">
                    <SelectItem value="all" className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">All muscle groups</SelectItem>
                    {sanitizedFilterOptions.primaryMuscleGroups.map((muscle) => (
                      <SelectItem key={muscle} value={muscle} className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">
                        {muscle}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Force Filter */}
              <div>
                <label className="text-sm font-medium mb-2 block text-gray-700">
                  Force Type
                </label>
                <Select
                  value={filters.force || undefined}
                  onValueChange={(value) => handleFilterChange('force', value === 'all' ? undefined : value as any)}
                  disabled={isLoading}
                >
                  <SelectTrigger className="bg-white border-gray-300 hover:border-gray-400 text-gray-900">
                    <SelectValue placeholder="All force types" />
                  </SelectTrigger>
                  <SelectContent className="bg-white border-gray-200 shadow-md">
                    <SelectItem value="all" className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">All force types</SelectItem>
                    {sanitizedFilterOptions.forces.map((force) => (
                      <SelectItem key={force} value={force} className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">
                        {force}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Level Filter */}
              <div>
                <label className="text-sm font-medium mb-2 block text-gray-700">
                  Difficulty Level
                </label>
                <Select
                  value={filters.level || undefined}
                  onValueChange={(value) => handleFilterChange('level', value === 'all' ? undefined : value)}
                  disabled={isLoading}
                >
                  <SelectTrigger className="bg-white border-gray-300 hover:border-gray-400 text-gray-900">
                    <SelectValue placeholder="All levels" />
                  </SelectTrigger>
                  <SelectContent className="bg-white border-gray-200 shadow-md">
                    <SelectItem value="all" className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">All levels</SelectItem>
                    {sanitizedFilterOptions.levels.map((level) => (
                      <SelectItem key={level} value={level} className="text-gray-900 focus:bg-gray-100 focus:text-gray-900">
                        {level}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CollapsibleContent>
        </Collapsible>
      </CardContent>
    </Card>
  );
};

export default ExerciseFilters;

'use client';

import { useState, useEffect, useCallback } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import ExerciseFilters from './ExerciseFilters';
import { ExerciseFilters as Filters, FilterOptions } from '../../types/exercise';

interface ExerciseFiltersClientProps {
  initialFilters: Filters;
  filterOptions: FilterOptions;
}

const ExerciseFiltersClient: React.FC<ExerciseFiltersClientProps> = ({
  initialFilters,
  filterOptions,
}) => {
  const [filters, setFilters] = useState<Filters>(initialFilters);
  const [isLoading, setIsLoading] = useState(false);
  const [isInitialLoad, setIsInitialLoad] = useState(true);
  const [previousFilters, setPreviousFilters] = useState<Filters>(initialFilters);
  const router = useRouter();
  const searchParams = useSearchParams();

  const filtersChanged = useCallback((oldFilters: Filters, newFilters: Filters) => {
    return (
      oldFilters.search !== newFilters.search ||
      oldFilters.exerciseType !== newFilters.exerciseType ||
      oldFilters.primaryMuscleGroup !== newFilters.primaryMuscleGroup ||
      oldFilters.force !== newFilters.force ||
      oldFilters.level !== newFilters.level
    );
  }, []);

  const updateURL = useCallback(() => {
    const params = new URLSearchParams(searchParams);

    // Update filter parameters
    if (filters.search) {
      params.set('search', filters.search);
    } else {
      params.delete('search');
    }

    if (filters.exerciseType) {
      params.set('type', filters.exerciseType);
    } else {
      params.delete('type');
    }

    if (filters.primaryMuscleGroup) {
      params.set('muscle', filters.primaryMuscleGroup);
    } else {
      params.delete('muscle');
    }

    if (filters.force) {
      params.set('movement', filters.force);
    } else {
      params.delete('movement');
    }

    if (filters.level) {
      params.set('difficulty', filters.level);
    } else {
      params.delete('difficulty');
    }

    // Only reset to page 1 when filters actually change
    if (filtersChanged(previousFilters, filters)) {
      params.delete('page');
      setPreviousFilters(filters);
    }

    const newURL = `/exercise-library?${params.toString()}`;
    router.push(newURL);
  }, [filters, searchParams, router, previousFilters, filtersChanged]);

  // Debounce filter changes (skip on initial load)
  useEffect(() => {
    if (isInitialLoad) {
      setIsInitialLoad(false);
      return;
    }

    const timeoutId = setTimeout(() => {
      updateURL();
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [filters, isInitialLoad, updateURL]);

  const handleFiltersChange = (newFilters: Filters) => {
    setFilters(newFilters);
  };

  const handleClearFilters = () => {
    setFilters({});
  };

  return (
    <ExerciseFilters
      filters={filters}
      filterOptions={filterOptions}
      onFiltersChange={handleFiltersChange}
      onClearFilters={handleClearFilters}
      isLoading={isLoading}
    />
  );
};

export default ExerciseFiltersClient;

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { Dumbbell } from 'lucide-react';
import { FadeIn } from '@/components/animations/FadeIn';
import { EmptyState } from '@/components/ui/empty-state';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import ExerciseFilters from './ExerciseFilters';
import ExerciseCard from './ExerciseCard';
import PaginationClient from './PaginationClient';
import { ExerciseFilters as Filters, FilterOptions, ExerciseListResponse } from '@/types/exercise';

interface ExerciseLibraryClientProps {
  initialData: ExerciseListResponse;
  initialFilters: Filters;
  filterOptions: FilterOptions;
  currentPage: number;
}

const ExerciseLibraryClient: React.FC<ExerciseLibraryClientProps> = ({
  initialData,
  initialFilters,
  filterOptions,
  currentPage,
}) => {
  const [exercises, setExercises] = useState(initialData.exercises);
  const [total, setTotal] = useState(initialData.total);
  const [filters, setFilters] = useState<Filters>(initialFilters);
  const [page, setPage] = useState(currentPage);
  const [isSearching, setIsSearching] = useState(false);
  const [searchError, setSearchError] = useState<string | null>(null);
  const [isUsingClientSearch, setIsUsingClientSearch] = useState(false);
  const [previousFilters, setPreviousFilters] = useState<Filters>(initialFilters);

  const router = useRouter();
  const searchParams = useSearchParams();

  // Check if filters have changed
  const filtersChanged = useCallback((oldFilters: Filters, newFilters: Filters) => {
    return (
      oldFilters.search !== newFilters.search ||
      oldFilters.exerciseType !== newFilters.exerciseType ||
      oldFilters.primaryMuscleGroup !== newFilters.primaryMuscleGroup ||
      oldFilters.force !== newFilters.force ||
      oldFilters.level !== newFilters.level
    );
  }, []);

  // Update URL when filters change (but only for non-search changes)
  const updateURL = useCallback((newFilters: Filters, newPage: number) => {
    const params = new URLSearchParams(searchParams);

    // Update filter parameters
    if (newFilters.search) {
      params.set('search', newFilters.search);
    } else {
      params.delete('search');
    }

    if (newFilters.exerciseType) {
      params.set('type', newFilters.exerciseType);
    } else {
      params.delete('type');
    }

    if (newFilters.primaryMuscleGroup) {
      params.set('muscle', newFilters.primaryMuscleGroup);
    } else {
      params.delete('muscle');
    }

    if (newFilters.force) {
      params.set('movement', newFilters.force);
    } else {
      params.delete('movement');
    }

    if (newFilters.level) {
      params.set('difficulty', newFilters.level);
    } else {
      params.delete('difficulty');
    }

    // Handle pagination
    if (newPage > 1) {
      params.set('page', newPage.toString());
    } else {
      params.delete('page');
    }

    const newURL = `/exercise-library?${params.toString()}`;
    router.push(newURL, { scroll: false });
  }, [searchParams, router]);

  // Fetch filtered data from API
  const fetchFilteredData = useCallback(async (newFilters: Filters, pageNum: number = 1) => {
    try {
      setIsSearching(true);
      setSearchError(null);

      const params = new URLSearchParams();

      if (newFilters.search) {
        params.set('search', newFilters.search);
      }

      if (newFilters.exerciseType) {
        params.set('type', newFilters.exerciseType);
      }

      if (newFilters.primaryMuscleGroup) {
        params.set('muscle', newFilters.primaryMuscleGroup);
      }

      if (newFilters.force) {
        params.set('movement', newFilters.force);
      }

      if (newFilters.level) {
        params.set('difficulty', newFilters.level);
      }

      params.set('page', pageNum.toString());
      params.set('limit', '15');

      const response = await fetch(`/api/exercises?${params.toString()}`);
      if (!response.ok) {
        throw new Error('Failed to fetch exercises');
      }

      const data = await response.json();

      setExercises(data.exercises);
      setTotal(data.total);
      setPage(pageNum);
      setIsUsingClientSearch(true);
      setIsSearching(false);

      // Update URL without page reload
      updateURL(newFilters, pageNum);

    } catch (error) {
      console.error('Error fetching filtered exercises:', error);
      setSearchError('Failed to load exercises. Please try again.');
      setIsSearching(false);
    }
  }, [updateURL]);

  // Listen to URL changes and fetch data accordingly
  useEffect(() => {
    const currentPageFromURL = parseInt(searchParams.get('page') || '1');
    const searchFromURL = searchParams.get('search') || '';
    const typeFromURL = searchParams.get('type') || '';
    const muscleFromURL = searchParams.get('muscle') || '';
    const movementFromURL = searchParams.get('movement') || '';
    const difficultyFromURL = searchParams.get('difficulty') || '';

    const urlFilters: Filters = {
      search: searchFromURL || undefined,
      exerciseType: typeFromURL || undefined,
      primaryMuscleGroup: muscleFromURL || undefined,
      force: movementFromURL as any || undefined,
      level: difficultyFromURL as any || undefined,
    };

    // Check if URL params differ from current state
    const paramsChanged = 
      currentPageFromURL !== page ||
      urlFilters.search !== filters.search ||
      urlFilters.exerciseType !== filters.exerciseType ||
      urlFilters.primaryMuscleGroup !== filters.primaryMuscleGroup ||
      urlFilters.force !== filters.force ||
      urlFilters.level !== filters.level;

    if (paramsChanged && !isUsingClientSearch) {
      // Update filters state to match URL
      setFilters(urlFilters);

      // Fetch new data for the URL parameters
      fetchFilteredData(urlFilters, currentPageFromURL);
    }
  }, [searchParams, page, filters, isUsingClientSearch, fetchFilteredData]);

  // Handle search results from debounced search
  const handleSearchResults = useCallback((results: ExerciseListResponse) => {
    // Non-blocking updates - results and URL are already handled by the hook
    setExercises(results.exercises);
    setTotal(results.total);
    setPage(1); // Reset to page 1 for search results
    setIsUsingClientSearch(true);
  }, []);

  // Handle search status changes
  const handleSearchStatusChange = useCallback((
    status: 'idle' | 'loading' | 'success' | 'error', 
    error?: string | null
  ) => {
    setIsSearching(status === 'loading');
    setSearchError(error || null);
  }, []);

  // Handle filter changes
  const handleFiltersChange = useCallback((newFilters: Filters) => {
    const hasFiltersChanged = filtersChanged(filters, newFilters);

    setFilters(newFilters);

    // If it's a search change, let the debounced search handle it
    if (newFilters.search !== filters.search) {
      // If search was cleared, we need to trigger the search immediately
      if (!newFilters.search || newFilters.search.trim().length === 0) {
        // This will be handled by the DebouncedSearchInput component
      }
      return;
    }

    // For non-search filter changes, fetch data via API
    if (hasFiltersChanged) {
      fetchFilteredData(newFilters, 1);
    }
  }, [filters, filtersChanged, fetchFilteredData]);

  // Handle clear filters
  const handleClearFilters = useCallback(() => {
    const clearedFilters: Filters = {};
    fetchFilteredData(clearedFilters, 1);
  }, [fetchFilteredData]);

  // Calculate total pages
  const totalPages = Math.ceil(total / 15);

  return (
    <>
      {/* Filters */}
      <div className="mb-12">
        <ExerciseFilters 
          filters={filters}
          filterOptions={filterOptions}
          onFiltersChange={handleFiltersChange}
          onClearFilters={handleClearFilters}
          onSearchResults={handleSearchResults}
          onSearchStatusChange={handleSearchStatusChange}
          isLoading={isSearching}
        />
      </div>

      {/* Search Error */}
      {searchError && (
        <div className="mb-8">
          <Alert variant="destructive">
            <AlertDescription>
              {searchError}
            </AlertDescription>
          </Alert>
        </div>
      )}

      {/* Results Summary */}
      <div className="mb-8">
        <p className="text-sm text-gray-600 text-center">
          {isSearching ? (
            'Searching...'
          ) : (
            <>
              Showing {exercises.length} of {total} exercises
              {page > 1 && ` (Page ${page} of ${totalPages})`}
              {isUsingClientSearch && filters.search && (
                <span className="ml-2 text-blue-600">‚Ä¢ Live search results</span>
              )}
            </>
          )}
        </p>
      </div>

      {/* Exercise Grid */}
      {exercises.length > 0 ? (
        <div className="container max-w-7xl mx-auto mb-12">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 justify-center">
            {exercises.map((exercise, index) => (
              <FadeIn key={exercise.id} delay={Math.min(index * 25, 200)}>
                <ExerciseCard exercise={exercise} />
              </FadeIn>
            ))}
          </div>
        </div>
      ) : (
        <div className="max-w-4xl mx-auto mb-12">
          <EmptyState
            icon={<Dumbbell className="w-12 h-12 text-gray-400" />}
            title={isSearching ? "Searching..." : "No Exercises Found"}
            description={
              isSearching 
                ? "Please wait while we search for exercises..."
                : "Try adjusting your filters or search terms to find exercises that match your criteria."
            }
            action={
              !isSearching ? (
                <Button 
                  variant="outline" 
                  onClick={handleClearFilters}
                  className="mt-4"
                >
                  Clear all filters
                </Button>
              ) : undefined
            }
          />
        </div>
      )}

      {/* Pagination - only show if not using client search or if search has multiple pages */}
      {totalPages > 1 && (!isUsingClientSearch || !filters.search) && (
        <div className="flex justify-center">
          <PaginationClient
            currentPage={page}
            totalPages={totalPages}
            filters={filters}
          />
        </div>
      )}
    </>
  );
};

export default ExerciseLibraryClient;

'use client';

import React from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { Button } from '../ui/button';
import { ExerciseFilters as Filters } from '../../types/exercise';

interface PaginationClientProps {
  currentPage: number;
  totalPages: number;
  filters: Filters;
}

const PaginationClient: React.FC<PaginationClientProps> = ({
  currentPage,
  totalPages,
  filters,
}) => {
  const router = useRouter();
  const searchParams = useSearchParams();

  // Get current page from URL to ensure it's always in sync
  const urlPage = parseInt(searchParams.get('page') || '1');
  const activePage = urlPage || currentPage;

  const navigateToPage = (page: number) => {
    const params = new URLSearchParams(searchParams);

    // Update filter parameters
    if (filters.search) {
      params.set('search', filters.search);
    } else {
      params.delete('search');
    }

    if (filters.exerciseType) {
      params.set('type', filters.exerciseType);
    } else {
      params.delete('type');
    }

    if (filters.primaryMuscleGroup) {
      params.set('muscle', filters.primaryMuscleGroup);
    } else {
      params.delete('muscle');
    }

    if (filters.force) {
      params.set('movement', filters.force);
    } else {
      params.delete('movement');
    }

    if (filters.level) {
      params.set('difficulty', filters.level);
    } else {
      params.delete('difficulty');
    }

    // Set page parameter
    if (page > 1) {
      params.set('page', page.toString());
    } else {
      params.delete('page');
    }

    const newURL = `/exercise-library?${params.toString()}`;
    router.push(newURL);
  };

  const renderPageNumbers = () => {
    const pages = [];
    const maxVisible = 5;

    let startPage = Math.max(1, activePage - Math.floor(maxVisible / 2));
    let endPage = Math.min(totalPages, startPage + maxVisible - 1);

    if (endPage - startPage + 1 < maxVisible) {
      startPage = Math.max(1, endPage - maxVisible + 1);
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(
        <Button
          key={i}
          variant={i === activePage ? "default" : "outline"}
          size="sm"
          onClick={() => navigateToPage(i)}
          className={i === activePage ? "bg-gray-800 hover:bg-gray-700 text-white" : "bg-white border-gray-300 text-gray-700 hover:bg-gray-50 hover:text-gray-900"}
        >
          {i}
        </Button>
      );
    }

    return pages;
  };

  return (
    <div className="flex justify-center items-center space-x-2 bg-white rounded-lg p-4 shadow-sm border border-gray-200">
      {activePage > 1 && (
        <Button 
          variant="outline" 
          size="sm" 
          onClick={() => navigateToPage(activePage - 1)}
          className="bg-white border-gray-300 text-gray-700 hover:bg-gray-50 hover:text-gray-900"
        >
          Previous
        </Button>
      )}

      {renderPageNumbers()}

      {activePage < totalPages && (
        <Button 
          variant="outline" 
          size="sm" 
          onClick={() => navigateToPage(activePage + 1)}
          className="bg-white border-gray-300 text-gray-700 hover:bg-gray-50 hover:text-gray-900"
        >
          Next
        </Button>
      )}
    </div>
  );
};

export default PaginationClient;

