      throw new Error('Failed to get exercise statistics');
    }
  }

  // Get service status
  getServiceStatus(): {
    database: boolean;
    s3: boolean;
    contentful: boolean;
  } {
    return {
      database: true, // Always available
      s3: !!s3Service,
      contentful: !!getMultipleExerciseContents,
    };
  }
}

